// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;
pragma experimental ABIEncoderV2;
contract Predictor {

    address public owner;

    //address payable public ownerAccount;

    constructor () {
        owner = msg.sender;
        //ownerAccount = payable(owner);
        //createMatch(101, 1, "India", 4, "Australia", 1);
    }

    modifier onlyOwner()
    {
        require(msg.sender == owner, "Function accessible only by the owner !");
        _;
    }

    enum status {
        CLOSED, OPEN, AWAITED, SUSPENDED, ENDED
    }

    struct BET {
        address player;
        uint value;
        uint option;
        bool settled;
    }

    struct MATCH {
        uint matchId;
        uint sport;
        uint totalBetsA;
        uint totalBetsB;
        uint betCountA;
        uint betCountB;
        string teamA;
        string teamB;
        uint teamAId;
        uint teamBId;
        status statusId;
        uint winner;
    }

    struct MATCHBETS {
        uint matchId;
        BET[] bets;
    }

    uint totalMatches = 0;
    mapping(uint => MATCH) public matchesMap;

    mapping(uint => MATCHBETS) public betsMap;

    uint[] matches;

    function createMatch (uint _matchId, uint _sport, string memory _teamA, uint _teamAId, string memory _teamB, uint _teamBId) public onlyOwner returns (bool success){
        require(matchesMap[_matchId].sport == 0, "Cannot create a match if it already exists");
        matchesMap[_matchId] = MATCH({
            matchId: _matchId,
            sport: _sport,
            teamA: _teamA,
            teamAId: _teamAId,
            teamB: _teamB,
            teamBId: _teamBId,
            totalBetsA: 0,
            totalBetsB: 0,
            betCountA: 0,
            betCountB: 0,
            statusId: status.OPEN,
            winner: 0
        });

        matches.push(_matchId);
        totalMatches++;
        return true;
    }

    function updateMatchStatus (uint _matchId, uint _statusId) public onlyOwner returns (bool){
        require(matchesMap[_matchId].sport > 0 && uint(matchesMap[_matchId].statusId) != uint(_statusId), "Cannot update the status of the match if it doesn't exist or the status is the same");
        matchesMap[_matchId].statusId = status(_statusId);
        return true;
    }

    function deleteMatch (uint _matchId) public onlyOwner returns (bool){
        require(matchesMap[_matchId].sport > 0, "Cannot delete the match if it does not exist");
        delete matchesMap[_matchId];
        delete betsMap[_matchId];

        for(uint i=0;i<matches.length;i++) {
            if(matches[i] == _matchId) {
                matches[i] = matches[matches.length - 1];
                matches.pop();
                break;
            }
        }

        return true;
    }

    function getAllMatches() public view returns (MATCH[] memory) {
        MATCH[] memory _matches = new MATCH[](matches.length);

        for(uint i=0;i<matches.length;i++) {
            uint _mid = matches[i];
            _matches[i] = matchesMap[_mid];
            //_matches.push(matchesMap[_mid]);
        }

        return _matches;

    }

    function getBalance() public view returns (uint){
        return address(this).balance;
    }

    function bet (uint _matchId, uint _team) public payable returns (bool) {
        require(matchesMap[_matchId].sport > 0, "Cannot bet on a match that does not exist");

        MATCH storage thisMatch = matchesMap[_matchId];

        require(thisMatch.statusId == status.OPEN, "Cannot bet on a match if it is not open");
        //Check if the user has already created a bet

        if(_team == thisMatch.teamAId) {
            thisMatch.betCountA++;
            thisMatch.totalBetsA += msg.value;
        } else if (_team == thisMatch.teamBId) {
            thisMatch.betCountB++;
            thisMatch.totalBetsB += msg.value;
        } else {
            return false;
        }

        betsMap[_matchId].bets.push(BET(msg.sender, msg.value, _team, false));


        //ownerAccount.transfer(msg.value);
        //(bool sent,) = owner.call{value: msg.value}("");
        //require(sent, "Failed to send Ether");
        return true;
    }

    function getBets (uint _matchId) public view returns (BET[] memory){
        return betsMap[_matchId].bets;
    }

    function setWinner (uint _matchId, uint _team) public onlyOwner returns (bool) {

        require(matchesMap[_matchId].statusId == status.CLOSED, "Cannot set the winner if the bets are open");
        MATCH storage thisMatch = matchesMap[_matchId];
        uint _winningTeamAmount;
        uint _losingTeamAmount;
        if(_team == thisMatch.teamAId) {
            thisMatch.winner = thisMatch.teamAId;
            _winningTeamAmount = thisMatch.totalBetsA;
            _losingTeamAmount = thisMatch.totalBetsB;
        } else if (_team == thisMatch.teamBId) {
            thisMatch.winner = thisMatch.teamBId;
            _winningTeamAmount = thisMatch.totalBetsB;
            _losingTeamAmount = thisMatch.totalBetsA;
        } else {
            return false;
        }

        thisMatch.statusId = status.AWAITED;
        calculatePayOuts(_matchId, thisMatch.winner, _winningTeamAmount, _losingTeamAmount);
        return true;

    }

    function calculatePayOuts(uint _matchId, uint _winner, uint _winningTeamAmount, uint _losingTeamAmount) public payable returns (bool){
        BET[] storage _allBets = betsMap[_matchId].bets;

        for(uint i=0;i<_allBets.length;i++) {

            BET storage _thisBet = _allBets[i];
            if(_thisBet.option != _winner) {
                _thisBet.settled = true;
                continue;
            }

            uint gain = _thisBet.value +  ((_thisBet.value * _losingTeamAmount) / _winningTeamAmount);
            payable(_thisBet.player).transfer(gain);
            _thisBet.settled = true;
        }

        matchesMap[_matchId].statusId = status.ENDED;
        return true;
    }
}
